import * as actionTypes from "../actionTypes";
import initialState from "./initialState";


export function getBlogReducer(state=initialState.currentBlog, action){

     switch (action.type){

         case actionTypes.GET_BLOG_SUCCESS:
             return action.payload
         default :
             return state;


     }

}
